CREATE OR REPLACE VIEW VIEW_PRE_AUDIT_HISTORY AS SELECT PAH.PREAH_ID,PAMP.PREAMP_DATA,PAH.PREAMP_PROJ_KEY,if(PAH.PREAMP_STATUS=1,'SUCCESS','FAILURE')AS STATUS,PAH.PREAMP_SQL_REC,PAH.PREAMP_EXE_TIME,ULD.ULD_LOGINID,PAH.PREAMP_TIMESTAMP 
FROM PRE_AUDIT_HISTORY PAH,PRE_AUDIT_MAIN_PROFILE PAMP,DEVELOPMENT.USER_LOGIN_DETAILS ULD WHERE PAH.PREAMP_ID=PAMP.PREAMP_ID AND ULD.ULD_ID=PAH.ULD_ID ORDER BY PAH.PREAMP_TIMESTAMP ASC 