CREATE OR REPLACE VIEW VIEW_PRE_PROD_AUDIT_HISTORY AS SELECT PPAH.PPAH_ID,PPAH.PPAH_SOURCE_TABLE_NAME,PPAH.PPAH_SOURCE_NO_OF_REC,PPAH.PPAH_DESTINATION_TABLE_NAME,PPAH.PPAH_DESTINATION_NO_OF_REC,if(PPAH.PPAH_SUCCESS_FLAG=1,'SUCCESS','FAILURE')AS STATUS,PPAH.PPAH_PSTIME,PPAH.PPAH_PETIME,PPAH.PPAH_EXETIME,ULD.ULD_LOGINID,PPAH.PPAH_TIMESTAMP 
FROM PRE_PROD_AUDIT_HISTORY PPAH,DEVELOPMENT.USER_LOGIN_DETAILS ULD WHERE ULD.ULD_ID=PPAH.ULD_ID ORDER BY PPAH.PPAH_TIMESTAMP ASC 
